        mPytania.add(new pytanie(39, "Przy oparzeniu parą wodną obu rąk należy: ", "A. wezwać pomoc i podawać środki przeciwbólowe. ", " ", "B. pokryć powierzchnię suchym, jałowym opatrunkiem. ", " ", "C. schładzać obficie wodą i wezwać pomoc. ", " ", "D. okryć folią „życia” w celu ochrony przed wychłodzeniem. ", " ", "E. posmarować tłuszczem lub alkoholem bo dobrze wchłania ciepło ", " ", 3));
        mPytania.add(new pytanie(40, "Oparzenie ciężkie to: ", "A. oparzenie prądem elektrycznym. ", " ", "B. oparzenie dróg oddechowych. ", " ", "C. oparzenie II stopnia >25 % powierzchni ciała. ", " ", "D. oparzenie III stopnia >10 % powierzchni ciała. ", " ", "E. wszystkie odpowiedzi są prawdziwe. ", " ", 5));
        mPytania.add(new pytanie(41, "Przy porażeniu prądem i odłączeniu źródła napięcia pierwszą czynnością przy poszkodowanym jest: ", "A. udrożnienie dróg oddechowych i PMS. ", " ", "B. ocena stanu świadomości poszkodowanego. ", " ", "C. stabilizacja kręgosłupa szyjnego, udrożnienie dróg oddechowych i prowadzenie oddechu zastępczego. ", " ", "D. nie podchodzimy i nie ruszamy poszkodowanego w obawie o pozostały ładunek elektryczny wokół poszkodowanego. ", " ", "E. wszystkie fałszywe.", " ", 2));
        mPytania.add(new pytanie(42, "O oparzeniu dróg oddechowych i zatruciu wziewnym świadczą następujące objawy: ", "A. duszność, kaszel. ", " ", "B. ślady sadzy na twarzy, w jamie ustnej i ślinie, opalone brwi i rzęsy. ", " ", "C. charakter zdarzenia. ", " ", "D. chrypka, świszczący oddech. ", " ", "E. wszystkie wymienione. ", " ", 5));
        mPytania.add(new pytanie(43, "Jaka jest najpoważniejsza wczesna komplikacja porażenia prądem elektrycznym zmiennym? ", "A. zaburzenia rytmu serca. ", " ", "B. uraz kręgosłupa szyjnego. ", " ", "C. wstrząs hipowolemiczny. ", " ", "D. niewydolność nerek. ", " ", "E. wszystkie prawdziwe. ", " ", 1));
