        mPytania.add(new pytanie(45, "Podczas prac budowlanych jeden z pracowników został ochlapany wapnem w okolicy twarzy, poprawna kolejność postępowania to:\n1) opłukanie twarzy wodą w celu usunięcia substancji;\n2) starcie suchą szmatką zaprawy z twarzy i okolicy oczu;\n3) usunięcie poszkodowanego ze strefy zagrożenia;\n4) delikatne przemywanie wodą, najlepiej mineralną;\n5) zdecydowane przemywanie oczu bieżącym strumieniem wody. \nPrawidłowa odpowiedź to:", "A. 1,2. ", " ", "B. 3,2,4. ", " ", "C. 3,2,5. ", " ", "D. 4,1,2,3. ", " ", "E. 3,5.", " ", 3));
        mPytania.add(new pytanie(46, "Postępowanie z osobą w stanie wychłodzenia – wskaż prawidłową sekwencję działania:\n1) przenieść do suchego, ciepłego pomieszczenia i zdjęcie zbędnego ubrania;\n2) podać ciepły napój najlepiej z alkoholem- działa rozgrzewająco;\n3) kontrola podstawowych czynności życiowych;\n4) zdecydowanymi ruchami rozcierać miejsca wychłodzone lub zalecić gimnastykę;\n5) ułożyć w pozycji poziomej, ograniczyć ruch i ogrzewać biernie. \nPrawidłowa odpowiedź to:", "A. 1,2,3,4,5. ", " ", "B. 1,3,2,5. ", " ", "C. 3,4,2. ", " ", "D. 2,4,3. ", " ", "E. 3,1,5. ", " ", 5));
        mPytania.add(new pytanie(47, "Podczas smażenia frytek doszło do ochlapania gorącym olejem całej dłoni, skuteczne postępowanie będzie polegało na:\n1) natychmiastowym odsunięciu poszkodowanego od naczynia z olejem;\n2) umyciu ręki pod bieżącą wodą z użyciem detergentu by zmyć olej;\n3) chłodzeniu ręki pod bieżącą wodą ok. 15 min;\n4) trzymaniu ręki w wiadrze z wodą;\n5) polewaniu ręki alkoholem, bo świetnie odprowadza ciepło. \nPrawidłowa odpowiedź to:", "A. 1,3. ", " ", "B. 1,2,3. ", " ", "C. 3,5. ", " ", "D. 4,5. ", " ", "E. 1,2,4,5. ", " ", 2));
        mPytania.add(new pytanie(48, "Powierzchnia głowy jest zróżnicowana w zależności od wieku, wskaż prawidłową odpowiedź, wartości procentowe są ułożone odpowiednio: osoba dorosła, dziecko, niemowlę: ", "A. 8%, 12%, 17%. ", " ", "B. 9%, 14%, 18%. ", " ", "C. 9%, 16%, 18%.", " ", "D. 11%, 18%, 20%. ", " ", "E. 12%, 19%, 21%. ", " ", 3));
        mPytania.add(new pytanie(49, "Podczas prac przeładunkowych jeden z pracowników został oblany ługiem sodowym, wskaż prawidłowe postępowanie:\n1) natychmiast polewasz wodą poszkodowanego;\n2) usuwasz w miejsce bezpieczne poszkodowanego;\n3) zdejmujesz odzież z poszkodowanego;\n4) posypujesz piachem ubranego poszkodowanego- piach wchłania ług sodowy;\n5) spłukujesz poszkodowanego i wzywasz pomoc.\nPrawidłowa odpowiedź to: ", "A. 1,5,2. ", " ", "B. 2,3,4. ", " ", "C. 2,3,5. ", " ", "D. 5,3,4.", " ", "E. 1,2,3,5.", " ", 3));
        mPytania.add(new pytanie(50, "Jesteś świadkiem wypadku samochodu- cysterny, na drogę wycieka płyn, który daje biało- żółty dym, podejmujesz działania polegające na:\n1) natychmiast udajesz się do kabiny by wyciągnąć kierowcę z kabiny;\n2) zatrzymujesz nadjeżdżające pojazdy;\n3) dzwonisz na nr 998 i podajesz informacje o zdarzeniu i numery tablicy informacyjnej z samochodu;\n4) jak najszybciej oddalasz się z miejsca zdarzenia by nie ulec zatruciu;\n5) oczekujesz na przybycie służb ratowniczych.\nPrawidłowa odpowiedź to:", "A. 1,5,2. ", " ", "B. 3,4,5. ", " ", "C. 3,2,4,5. ", " ", "D. 3,2,1. ", " ", "E. 3,2,5. ", " ", 5));
        mPytania.add(new pytanie(51, "Po spożyciu przez poszkodowanego dużej ilości leków w celach samobójczych należy jak najszybciej: ", "A. zapytać poszkodowanego o powód i rozmawiać z nim by nie zasnął. ", " ", "B. prowokować wymioty w celu eliminacji trucizny pod warunkiem że jest przytomny, dbając by się nie zachłysnął. ", " ", "C. zabezpieczyć pozostałe opakowania po środkach by przekazać je ratownikom. ", " ", "D. wezwać pogotowie ratunkowe i czekać na przyjazd. ", " ", "E. wezwać policję.", " ", 2));
        mPytania.add(new pytanie(52, "Osobę po spożyciu dużej ilości alkoholu, należy: ", "A. chronić przed upadkiem, wychłodzeniem. ", " ", "B. w przypadku utraty świadomości, ułożyć w pozycji bezpiecznej. ", " ", "C. prowokować wymioty w celu eliminacji alkoholu i zmniejszenie efektu toksycznego. ", " ", "D. prawdziwe są odpowiedzi A,B,C. ", " ", "E. prawdziwe są odpowiedzi A,B. ", " ", 5));
        mPytania.add(new pytanie(53, "Czy jest różnica w postępowaniu z osoba oparzoną silnym kwasem lub silną zasadą? ", "A. nie ma różnicy, w obu przypadkach musimy najpierw zobojętnić substancję żrącą. ", " ", "B. przy oparzeniu kwasem mamy więcej czasu na podjęcie działań. ", " ", "C. zasada jest mniej groźna. ", " ", "D. postępowanie w obu przypadkach jest takie samo. ", " ", "E. wszystko zależy od stężenia substancji żrącej. ", " ", 4));
        mPytania.add(new pytanie(54, "Oparzenie I° charakteryzuje się: ", "A. pęcherzami z płynem surowiczym. ", " ", "B. piekącym, swędzącym rumieniem na skórze, bólem przy dotyku. ", " ", "C. brakiem czucia. ", " ", "D. wszystkie odpowiedzi są prawdziwe. ", " ", "E. prawdziwe są odpowiedzi A,B. ", " ", 2));
        mPytania.add(new pytanie(55, "Oparzenie II° charakteryzuje się: ", "A. piekącym, rumieniem na skórze, bólem przy dotyku. ", " ", "B. martwicą tkanek, brakiem czucia. ", " ", "C. pęcherzami wypełnionymi płynem na podłożu rumieniowym. ", " ", "D. wszystkie prawdziwe. ", " ", "E. prawdziwe są odpowiedzi A, C. ", " ", 3));
