        mPytania.add(new pytanie(57, "Jesteś świadkiem palenia opon z okazji „sobótek”: ", "A. akceptujesz zdarzenie bo taka jest tradycja. ", " ", "B. proponujesz by to zgaszono bo może być pożar. ", " ", "C. podczas spalania wydzielają się substancje toksyczne szkodliwe do zdrowia i środowiska. ", " ", "D. właściwie co Cię to obchodzi, Tobie nie szkodzi. ", " ", "E. prawdziwe są odpowiedzi B, C. ", " ", 5));
        mPytania.add(new pytanie(58, "Na wycieczce w lesie doszło do ukąszenia przez żmiję jednego z uczestników. Należy natychmiast: ", "A. naciąć nożem ranę i wyssać krew. ", " ", "B. wycisnąć jad razem z krwią. ", " ", "C. założyć opatrunek osłonowy i unieruchomić kończynę, udać się do szpitala z poszkodowanym. ", " ", "D. wszystkie odpowiedzi są poprawne i możliwe do zastosowania. ", " ", "E. prawdziwe są odpowiedzi A i B. ", " ", 3));
        mPytania.add(new pytanie(59, "Dziecko w trakcie zabawy w ogrodzie zostało ukąszone wielokrotnie przez pszczoły. Należy natychmiast: ", "A. odgonić pszczoły od dziecka i spytać co się stało. ", " ", "B. przenieść dziecko w miejsce bezpieczne i wezwać pomoc. ", " ", "C. przenieść dziecko w miejsce bezpieczne, w miarę możliwości usunąć pozostałe żądła i zrobić zimne okłady. ", " ", "D. uspokoić dziecko, obserwować oddech i wezwać pomoc lub natychmiast jechać do ośrodka pomocy medycznej. ", " ", "E. prawdziwe są odpowiedzi C,D.", " ", 5));
        mPytania.add(new pytanie(60, "Na dworcu PKP przy ławce stoi pakunek, odjechał kolejny pociąg i nie ma właściciela, Twoje postępowanie: ", "A. sprawdzasz zawartość i potem powiadamiasz służby porządkowe. ", " ", "B. po sprawdzeniu zawartości przenosisz pakunek do dyżurnego ruchu. ", " ", "C. zabierasz ze sobą bo może jest tam coś wartościowego. ", " ", "D. natychmiast zawiadamiasz służby porządkowe i nie zbliżasz się do opakowania. ", " ", "E. normalne ze ktoś zapomniał pakunku i zaraz po niego przyjdzie.", " ", 4));
        mPytania.add(new pytanie(61, "Usłyszałeś, że w sklepie jest podłożona bomba, natychmiast: ", "A. udajesz się na miejsce by zobaczyć działania pirotechników. ", " ", "B. idziesz zobaczyć jak wygląda bomba, bo to jak zwykle alarm fałszywy. ", " ", "C. oddalasz się z miejsca i namawiasz innych by nie utrudniać działań.", " ", "D. pomagasz w wynoszeniu rzeczy ze sklepu by ratować mienie. ", " ", "E. zgłaszasz się do dowódcy akcji że możesz im pomóc. ", " ", 3));
        mPytania.add(new pytanie(62, "W masywnych krwotokach (duża utrata krwi), spodziewamy się u poszkodowanego:\n1) zaburzeń świadomości;\n2) przyśpieszonego tętna;\n3) zaczerwienienia skóry;\n4) sinicy twarzy.\nPrawidłowa odpowiedź to:", "A. 1,2,3. ", " ", "B. 1,3. ", " ", "C. 1,2. ", " ", "D. 1,4. ", " ", "E. 2,4. ", " ", 3));
        mPytania.add(new pytanie(63, "Przy oparzeniach elektrycznych najważniejsze dla poszkodowanego są: ", "A. skutki miejscowe, jak np. niewielkie oparzenie dłoni. ", " ", "B. następstwa dla układu krążenia. ", " ", "C. niekorzystne działanie prądu na skórę (późniejsze blizny). ", " ", "D. wpływ prądu elektrycznego na potencjały mózgowe. ", " ", "E. wszystkie wymienione. ", " ", 2));
        mPytania.add(new pytanie(64, "Przy oparzeniach termicznych dłoni istotnymi elementami działań ratowniczych są:\n1) schładzanie bieżącą wodą do 15-20 min. lub ustąpienia bólu;\n2) zdjęcie biżuterii z palców;\n3) okrycie rany oparzeniowej po schłodzeniu jałowym opatrunkiem. Prawidłowa odpowiedź to:", "A. 1,3. ", " ", "B. 1,2. ", " ", "C. 2,3. ", " ", "D. wszystkie wymienione. ", " ", "E. tylko 1.", " ", 4));
        mPytania.add(new pytanie(65, "U nieprzytomnego poszkodowanego, po urazie, bez krwotoków zewnętrznych, z widocznym powierzchniowym otarciem skóry głowy, z zachowanym własnym wydolnym oddechem i tętnem – do działań pilnych należy:\n1) tlenoterapia;\n2) odkażenie rany;\n3) stabilizacja kręgosłupa szyjnego;\n4) ułożenie w pozycji przeciwwstrząsowej;\n5) założenie opatrunku osłaniającego.\nPrawidłowa odpowiedź to:", "A. 3,1. ", " ", "B. 1,4. ", " ", "C. 1,2,3. ", " ", "D. 2,3,5. ", " ", "E. 1,2,3,4. ", " ", 1));
        mPytania.add(new pytanie(66, "Spośród niżej wymienionych czynności ratowniczych, wskaż priorytetowe: ", "A. opatrzenie rany oparzeniowej. ", " ", "B. udrożnienie dróg oddechowych. ", " ", "C. unieruchomienie złamanej kończyny. ", " ", "D. zatamowanie krwotoku tętniczego. ", " ", "E. czynności ujęte w punktach B i D mają zawsze pierwszeństwo przed innymi działaniami. ", " ", 5));
        mPytania.add(new pytanie(67, "Ranę kłutą klatki piersiowej na miejscu zdarzenia zaopatrzysz: ", "A. opatrunkiem okrężnym z opaski dzianej. ", " ", "B. opatrunkiem z folii, szczelnie przymocowanym do klatki piersiowej ze wszystkich stron. ", " ", "C. pozostawiasz bez zaopatrzenia ze względu na niebezpieczeństwo braku przepływu powietrza", "w drogach oddechowych poszkodowanego.  ", "D. opatrunkiem zastawkowym. ", " ", "E. opatrunkiem uciskowym dla stabilizacji żeber.", " ", 4));
        mPytania.add(new pytanie(68, "W krwotoku tętniczym przedramienia, który nie zmniejsza się po rutynowym zaopatrzeniu opatrunkiem uciskowym, należy rozważyć następujące postępowanie:\n1) umieszczenie kończyny powyżej poziomu serca;\n2) wzmocnienie ucisku poprzez owinięcie opatrunku dodatkowymi zwojami bandaża;\n3) zdjęcie już założonego opatrunku i ponowne założenie grubszego opatrunku;\n4) założenie opaski zaciskowej na przedramieniu;\n5) założenie opaski zaciskowej na ramieniu.\nPrawidłowa odpowiedź to: ", "A. 1,3. ", " ", "B. 1,2,4. ", " ", "C. 1,2,5. ", " ", "D. 1,3,4. ", " ", "E. 1,3,5. ", " ", 3));
        mPytania.add(new pytanie(69, "Podawanie tlenu poszkodowanemu jest w określonych sytuacjach bezwzględnie konieczne. Spośród niżej przedstawionych wersji wybierz właściwą:\n1) tlen podawać można tylko poszkodowanemu, znajdującemu się w pozycji leżącej;\n2) podawanie tlenu jest podstawowym działaniem w zatruciach wziewnych; \n3) aby uzyskać ok. 100% stężenie tlenu podawanego dorosłemu przez maskę do tlenoterapii należy zastosować przepływ minimum 6 litrów na minutę;\n4) tlen jest szkodliwy przy dłuższym stosowaniu;\n5) zestaw do tlenoterapii biernej powinien posiadać rezerwuar.\nPrawidłowa odpowiedź to:", "A. 2,3,4. ", " ", "B. 1,2,4. ", " ", "C. 2,4,5. ", " ", "D. 1,4,5. ", " ", "E. 1,3,4 ", " ", 3));
        mPytania.add(new pytanie(70, "U poszkodowanego w wyniku wypadku, stwierdzono szereg obrażeń i objawów. Zaznacz, który objaw, (lub grupa objawów), albo obrażenie Twoim zdaniem jest najbardziej niepokojący i może wskazywać na potencjalne zagrożenie poszkodowanego: ", "A. złamanie kończyny górnej ze znacznym przemieszczeniem. ", " ", "B. oparzenie II stopnia okolicy goleni. ", " ", "C. rana szarpana dłoni z niewielkim, powolnym wyciekiem krwi. ", " ", "D. blada, chłodna i spocona skóra. ", " ", "E. złamanie otwarte goleni lewej bez krwotoku.", " ", 4));
        mPytania.add(new pytanie(71, "Wyciek płynu mózgowo-rdzeniowego i/lub krwawienie z uszu u poszkodowanego po urazie: ", "A. grozi rozwinięciem wstrząsu. ", " ", "B. świadczyć może o złamaniu podstawy czaszki. ", " ", "C. wymaga założenia jałowego tamponu do ucha. ", " ", "D. prawdziwe są odpowiedzi A,B i C. ", " ", "E. żadna z odpowiedzi nie jest prawdziwa. ", " ", 2));
        mPytania.add(new pytanie(72, "Krwotok tętniczy zaopatrujemy w pierwszej kolejności : ", "A. opaską zaciskową poniżej miejsca zranienia. ", " ", "B. opaską zaciskową powyżej miejsca zranienia. ", " ", "C. opatrunkiem uciskowym w miejscu zranienia. ", " ", "D. szczelnym opatrunkiem osłaniającym. ", " ", "E. prawdziwe są odpowiedzi A i C. ", " ", 3));
        mPytania.add(new pytanie(73, "W przypadku krwotoku u poszkodowanego występują pewne charakterystyczne objawy. Wskaż, który z niżej wymienionych objawów raczej nie wystąpi u takiego poszkodowanego: ", "A. przyspieszone tętno ", " ", "B. przyspieszony i spłycony oddech. ", " ", "C. odczuwalne wzmożone pragnienie. ", " ", "D. zwolniona czynność serca. ", " ", "E. uczucie zimna. ", " ", 4));
        mPytania.add(new pytanie(74, "W przypadku udzielania pomocy w oparzeniu, miejsce urazu należy schłodzić bieżącą wodą lub użyć opatrunku hydrożelowego. Cechą takiego postępowania jest to, że: ", "A. zmniejsza uczucie bólu. ", " ", "B. opatrunek nie przywiera do rany. ", " ", "C. zmniejsza znacznie ryzyko wystąpienia hipotermii spowodowanej schładzaniem. ", " ", "D. prawdziwe są odpowiedzi A i B. ", " ", "E. prawdziwe są odpowiedzi A, B i C.", " ", 5));
        mPytania.add(new pytanie(75, "W wyniku zdarzenia drogowego poszkodowany leży na ulicy. Stwierdzasz ranę tłuczoną głowy, złamanie kości obu goleni na tej samej wysokości oraz stłuczenie barku. Jeden ze stojących obok samochodów ma zbitą przednią szybę i wgniecioną maskę. Najbardziej prawdopodobną przyczyną doznanych obrażeń jest: ", "A. wypadnięcie poszkodowanego z jadącego samochodu. ", " ", "B. zgniecenie samochodu, w którym znajdował się poszkodowany. ", " ", "C. potrącenie poszkodowanego przez samochód osobowy. ", " ", "D. na podstawie takiego obrazu nie można przypuszczać jaki był mechanizm urazu. ", " ", "E. analiza przyczyn obrażeń nie ma znaczenia. ", " ", 3));
        mPytania.add(new pytanie(76, "Najdogodniejszą pozycją dla poszkodowanych po urazie brzucha jest pozycja: ", "A. leżąca z nogami zgiętymi w stawach biodrowych i kolanowych. ", " ", "B. półsiedząca. ", " ", "C. boczna bezpieczna. ", " ", "D. leżąca na brzuchu, z nogami wyprostowanymi. ", " ", "E. przeciwwstrząsowa ", " ", 1));
        mPytania.add(new pytanie(77, "U poszkodowanego po urazie głowy z ucha wycieka powoli różowo podbarwiony płyn. Podaj właściwy sposób postępowania: ", "A. stan taki nie wymaga na miejscu zdarzenia żadnych szczególnych działań ratowniczych,", "gdyż wyciek ustaje po kilku minutach.  ", "B. wykonać należy opatrunek osłaniający z gazy jałowej. ", " ", "C. wykonać należy opatrunek uciskowy z gazy jałowej z użyciem codofixu. ", " ", "D. należy do przewodu słuchowego włożyć szczelny tampon z gazy jałowej, aby zapobiec dalszemu wyciekowi. ", " ", "E. żadna z odpowiedzi nie jest prawdziwa. ", " ", 2));
        mPytania.add(new pytanie(78, "Krwotok tętniczy można próbować odróżnić od żylnego na podstawie: ", "A. intensywności wypływu krwi. ", " ", "B. większej bolesności przy krwawieniu. ", " ", "C. różnicy koloru wypływającej krwi. ", " ", "D. krwotok tętniczy ma zawsze charakter tętniący. ", " ", "E. prawdziwe są odpowiedzi A i C.", " ", 5));
        mPytania.add(new pytanie(79, "Poszkodowany siedzi w rozbitym samochodzie, jest blady, spocony, ma przyśpieszony oddech, złamane obie kończyny dolne oraz zaburzenia świadomości. Podaj prawidłowy sposób postępowania przed przybyciem jednostek ochrony zdrowia: ", "A. w pierwszej kolejności należy unieruchomić złamania, później wykonywać dalsze czynności. ", " ", "B. należy podać poszkodowanemu tlen i zaczekać z dalszym działaniem na przybycie lekarza. ", " ", "C. należy pilnie ewakuować poszkodowanego z pojazdu, bo pozycja, w jakiej się znajduje stanowi dla niego zagrożenie,", "a następnie wykonywać dalsze, określone rodzajem obrażeń procedury ratownicze.  ", "D. jeżeli pilna ewakuacja z pojazdu jest niemożliwa, należy przynajmniej nadać poszkodowanemu pozycję leżącą. ", " ", "E. prawdziwe są odpowiedzi C i D. ", " ", 5));
        mPytania.add(new pytanie(80, "Określ, która odpowiedź zawiera prawidłową kolejność postępowania z poszkodowanym po urazie w wyniku wypadku samochodowego: ", "A. pilne unieruchomienie złamań, zatamowanie krwotoku, zapewnienie komfortu termicznego. ", " ", "B. zabezpieczenie miejsca zdarzenia, dotarcie do poszkodowanego, udzielenie pomocy. ", " ", "C. zabezpieczenie miejsca zdarzenia, unieruchomienie złamań, podanie tlenu. ", " ", "D. stabilizacja głowy przy pomocy kołnierza ortopedycznego, tamowanie krwotoku, unieruchomienie złamań. ", " ", "E. z podanych wyżej kolejności żadna nie jest prawidłowa. ", " ", 2));
        mPytania.add(new pytanie(81, "Kołnierz ortopedyczny służy do: ", "A. stabilizacji odcinka szyjnego kręgosłupa. ", " ", "B. usztywnienia kręgosłupa szyjnego. ", " ", "C. unieruchomienia głowy. ", " ", "D. unieruchomienia głowy tylko na czas ewakuacji. ", " ", "E. żadna z odpowiedzi nie jest prawdziwa.", " ", 1));
        mPytania.add(new pytanie(82, "U poszkodowanego z rozległą raną powłok i obrażeniami narządów jamy brzusznej ratownik powinien: ", "A. podać doustnie ogrzane, niesłodzone płyny celem zmniejszenia utraty ciepła. ", " ", "B. założyć opatrunek osłaniający przykryty folią termoizolacyjną. ", " ", "C. założyć opatrunek uciskowy przykryty folią termoizolacyjną. ", " ", "D. prawdziwe są odpowiedzi A i C. ", " ", "E. prawdziwe są odpowiedzi A i B. ", " ", 2));
        mPytania.add(new pytanie(83, "Spośród wymienionych czynności, do zakresu kwalifikowanej pierwszej pomocy medycznej udzielanej przez ratowników, nie należy: ", "A. wykonywanie pośredniego masażu serca. ", " ", "B. podejmowanie decyzji o transporcie poszkodowanych do ośrodków leczniczych. ", " ", "C. tamowanie krwotoków. ", " ", "D. udrożnienie dróg oddechowych z użyciem rurki ustno-gardłowej. ", " ", "E. zapewnienie komfortu cieplnego. ", " ", 2));
        mPytania.add(new pytanie(84, "W masywnych krwotokach (duża utrata krwi), spodziewamy się u poszkodowanego: ", "A. zaburzeń świadomości. ", " ", "B. przyspieszonego tętna. ", " ", "C. zaczerwienienia skóry. ", " ", "D. bladości skóry. ", " ", "E. prawdziwe są odpowiedzi A,B i D. ", " ", 5));
        mPytania.add(new pytanie(85, "W złamaniu otwartym kości udowej, któremu towarzyszy krwotok tętniczy, priorytetem ratowniczym jest: ", "A. zatamowanie krwotoku, w razie potrzeby przez ucisk na tętnicę powyżej miejsca złamania. ", " ", "B. ułożenie odłamów w pozycji zbliżonej do fizjologicznej dla stworzenia warunków", "dla zastosowania opatrunku uciskowego.  ", "C. założenie opatrunku osłaniającego i stabilizacja w pozycji zbliżonej do fizjologicznej. ", " ", "D. stabilizacja i unieruchomienie w pozycji zastanej oraz opatrunek uciskowy. ", " ", "E. tlenoterapia 100% tlenem", " ", 1));
        mPytania.add(new pytanie(86, "Poszkodowanemu w hipotermii należy zapewnić pozycję: ", "A. poziomą. ", " ", "B. półsiedzącą. ", " ", "C. przeciwwstrząsową. ", " ", "D. boczną bezpieczną. ", " ", "E. pozycja nie ma znaczenia. ", " ", 1));
        mPytania.add(new pytanie(87, "W razie zwichnięcia stawu należy: ", "A. nakazać delikatne ruchy czynne, po kilkunastu minutach ból ustąpi. ", " ", "B. nastawić zwichnięcie, podciągając w osi kończyny, staw rozmasować. ", " ", "C. unieruchomić staw w pozycji fizjologicznej. ", " ", "D. unieruchomić staw w pozycji zastanej. ", " ", "E. dla zmniejszenia obrzęku zastosować opatrunek z bandaża elastycznego. ", " ", 4));
        mPytania.add(new pytanie(88, "Kołnierz szyjny służy do: ", "A. stabilizacji kręgosłupa na odcinku szyjnym. ", " ", "B. unieruchomienia kręgosłupa na odcinku szyjnym i piersiowym. ", " ", "C. unieruchomienia kręgosłupa na odcinku szyjnym. ", " ", "D. usztywnieniu kręgosłupa na odcinku szyjnym. ", " ", "E. ochrony przed następstwami doznanego urazu kręgosłupa szyjnego. ", " ", 1));
        mPytania.add(new pytanie(89, "W przypadku braku szyn Kramera, złamaną goleń: ", "A. pozostawiamy bez zaopatrzenia. ", " ", "B. unieruchamiamy w pozycji zastanej przy użyciu kija, deski itp. sprzętu przygodnego. ", " ", "C. owijamy ściśle bandażem elastycznym. ", " ", "D. mocujemy do drugiej kończyny. ", " ", "E. mocujemy do drugiej kończyny przy użyciu przekładki pomiędzy nimi. ", " ", 5));
        mPytania.add(new pytanie(90, "Uszkodzoną kończynę górną możesz unieruchomić: ", "A. szyną Kramera. ", " ", "B. na chuście trójkątnej. ", " ", "C. przez przybandażowanie kończyny do klatki piersiowej. ", " ", "D. przy użyciu sprzętu przygodnego. ", " ", "E. wszystkie odpowiedzi są prawdziwe.", " ", 5));
        mPytania.add(new pytanie(91, "Leżącego poszkodowanego po urazie kręgosłupa ratownik wyposażony w sprzęt medyczny: ", "A. powinien zawsze ułożyć w pozycji bezpiecznej (bocznej ustalonej), gdyż zabezpiecza ona przed dalszymi urazami. ", " ", "B. powinien zawsze ułożyć na plecach na miękkim podłożu. ", " ", "C. powinien zawsze ułożyć na plecach, założyć kołnierz szyjny i uważać,", "aby przy przenoszeniu nie powodować zmian pozycji w osi kręgosłupa.  ", "D. powinien, po założeniu kołnierza ortopedycznego, niezwłocznie doprowadzić do pozycji siedzącej", "w celu stabilizacji grawitacyjnej.  ", "E. żadna z odpowiedzi nie jest prawdziwa. ", " ", 5));
        mPytania.add(new pytanie(92, "Podejrzewasz uraz kręgosłupa w przypadku: ", "A. wypadek komunikacyjny, uraz głowy, złamanie kończyny dolnej. ", " ", "B. wypadek komunikacyjny, uraz głowy, upadek z wysokości. ", " ", "C. wypadek komunikacyjny, uraz głowy, rana brzucha. ", " ", "D. nie ma prawdziwej odpowiedzi. ", " ", "E. prawdziwe są odpowiedzi A, B i C. ", " ", 5));
        mPytania.add(new pytanie(93, "Opatrunek uciskowy stosuje się: ", "A. w przypadku krwotoków zewnętrznych. ", " ", "B. w przypadku krwotoków wewnętrznych. ", " ", "C. jedynie w przypadku krwotoków żylnych. ", " ", "D. jedynie w przypadku krwotoków z kończyn ", " ", "E. jednocześnie z opaską zaciskową. ", " ", 1));
        mPytania.add(new pytanie(94, "W przypadku rozpoznania wstrząsu krwotocznego postępowanie przeciwwstrząsowe obejmuje: ", "A. nadanie pozycji poziomej z uniesieniem kończyn dolnych tlenoterapię. ", " ", "B. nie ma takiego postępowania. ", " ", "C. uniesienie kończyn dolnych, tlenoterapię, wsparcie psychiczne. ", " ", "D. uniesienie kończyn dolnych, wsparcie psychiczne, zastosowanie folii termoizolacyjnej,", "zastosowanie rurki ustno-gardłowej.  ", "E. tak jak w punkcie A, ale po zatamowaniu krwawienia zewnętrznego.", " ", 5));
        mPytania.add(new pytanie(95, "W postępowaniu przeciwwstrząsowym najważniejsze jest: ", "A. wspomaganie psychiczne. ", " ", "B. termoizolacja. ", " ", "C. ułożenie w pozycji przeciwwstrząsowej. ", " ", "D. tlenoterapia. ", " ", "E. wszystkie odpowiedzi są jednakowo ważne. ", " ", 3));
        mPytania.add(new pytanie(96, "Procedury ewakuacyjne zastosujesz: ", "A. gdy poszkodowany znajduje się w strefie zagrożenia. ", " ", "B. tylko i wyłącznie na polecenie i w obecności przybyłego lekarza. ", " ", "C. w sytuacji zagrożenia, wynikającej z niekorzystnej pozycji zajmowanej przez poszkodowanego. ", " ", "D. wobec każdego poszkodowanego pozostającego w pojeździe zdeformowanym poszkodowanego", "w wyniku wypadku drogowego.  ", "E. ewakuować poszkodowanego trzeba zawsze w sytuacjach opisanych w odpowiedziach A i C. ", " ", 1));
        mPytania.add(new pytanie(97, "Podczas ewakuacji poszkodowanego na noszach – desce, powinny być spełnione określone warunku. Wskaż wśród niżej wymienionych twierdzeń błędne: ", "A. przed jakimkolwiek przemieszczeniem poszkodowanego na desce, należy zapiąć 4 pasy mocujące. ", " ", "B. przenosić poszkodowanego należy – w miarę możliwości – nogami w kierunku marszu. ", " ", "C. dla unieruchomienia głowy poszkodowanego w trakcie takiej ewakuacji wystarczy jedynie kołnierz ortopedyczny. ", " ", "D. należy unikać kolejnego przekładania poszkodowanego na nosze karetki pogotowia; zgodnie z „zasadą jednych noszy”. ", " ", "E. ręce poszkodowanego powinny być przymocowane pasami. ", " ", 3));
        mPytania.add(new pytanie(98, "Poszkodowanego w wypadku drogowym ewakuuje się z wnętrza pojazdu, gdy: ", "A. dowódca wyda polecenie. ", " ", "B. występuje konieczność resuscytacji. ", " ", "C. poszkodowany jest w stanie wstrząsu. ", " ", "D. występuje zagrożenie zewnętrzne. ", " ", "E. prawdziwe są odpowiedzi B,C i D.", " ", 5));
        mPytania.add(new pytanie(99, "Dla ratownika różnica pomiędzy transportem a ewakuacją polega na tym, że: ", "A. każde przemieszczenie poszkodowanego na mocy decyzji ratownika to ewakuacja,", "a na mocy decyzji lekarza lub ratownika medycznego to transport.", "B. przemieszczanie poszkodowanego na noszach to zawsze ewakuacja,", "a przemieszczenie przy wykorzystaniu karetki – to transport.", "C. strażacy zawsze ewakuują, a zespoły ratownictwa medycznego transportują. ", " ", "D. prawdziwe są odpowiedzi A i C. ", " ", "E. ewakuacja i transport to dwie nazwy tego samego zjawiska. ", " ", 1));
        mPytania.add(new pytanie(100, "Określona w procesie segregacji poszkodowanych w zdarzeniu masowym grupa „czerwona” to grupa o najwyższym priorytecie: ", "A. transportowym. ", " ", "B. terapeutycznym i transportowym. ", " ", "C. reanimacyjnym. ", " ", "D. prawdziwe są odpowiedzi A,B i C. ", " ", "E. segregacja to wyznaczanie kolejności, a nie priorytetów ", " ", 2));
        mPytania.add(new pytanie(101, "Zdarzenie masowe to zdarzenie o znacznej dysproporcji pomiędzy zapotrzebowaniami na medyczne działania ratownicze realizowane w trybie natychmiastowym a możliwościami: ", "A. sił i środków podmiotów ratowniczych obecnych na miejscu zdarzenia. ", " ", "B. sił i środków ratowniczych powiatu. ", " ", "C. sił i środków ratowniczych województwa. ", " ", "D. sił i środków ujętych w planie ratowniczym dla obszaru chronionego. ", " ", "E. zespołów ratownictwa medycznego obecnych na miejscu zdarzenia. ", " ", 1));
        mPytania.add(new pytanie(102, "W wyniku wypadku autobusu 7 osób zostało poszkodowanych. Określimy to zdarzenie jako: ", "A. masowe. ", " ", "B. mnogie. ", " ", "C. w zależności od określonych w wyniku segregacji obrażeń i możliwości ratowników na miejscu zdarzenia. ", " ", "D. masowe, powyżej 10 poszkodowanych to katastrofa. ", " ", "E. mnogie, powyżej 10 poszkodowanych to katastrofa.", " ", 3));
        mPytania.add(new pytanie(103, "Umieszczona w planie ratowniczym procedura dysponowania danego podmiotu ratowniczego oparta powinna być głównie na:\n1) odległości miejsca stacjonowania podmiotu od miejsca zdarzenia;\n2) przynależności administracyjnej miejsca zdarzenia;\n3) teoretycznym czasie przybycia podmiotu ratowniczego na miejsce zdarzenia;\n4) rodzaju zdarzenia;\n5) aktualnym czasie przybycia podmiotu ratowniczego na miejsce zdarzenia.\nPrawidłowa odpowiedź to:", "A. 1,4. ", " ", "B. 2,4. ", " ", "C. 4,5. ", " ", "D. 1,4. ", " ", "E. 2,5. ", " ", 3));
        mPytania.add(new pytanie(104, "Poszkodowany wydolny krążeniowo i oddechowo z zamkniętym złamaniem goleni, u którego po kilkunastu minutach po segregacji pierwotnej rozwinęły się objawy wstrząsu, należy do grupy: ", "A. najpierw żółtej, potem czerwonej. ", " ", "B. najpierw czerwonej, potem żółtej. ", " ", "C. cały czas żółtej, z uwagi na rodzaj obrażeń wiodących. ", " ", "D. cały czas czerwonej, z uwagi na rodzaj obrażeń wiodących. ", " ", "E. najpierw żółtej, potem czerwonej z uwagi na stan poszkodowanego. ", " ", 5));
        mPytania.add(new pytanie(105, "W założeniach taktycznych ratownictwa medycznego w zdarzeniach na drogach, wykonanie dostępu oznacza: ", "A. dotarcie do poszkodowanego. ", " ", "B. stworzenie możliwości zbadania i przemieszczania poszkodowanego. ", " ", "C. stworzenie możliwości zbadania poszkodowanego. ", " ", "D. to samo, co „wycięcie poszkodowanego z pojazdu”. ", " ", "E. zabezpieczenie miejsca zdarzenia i umożliwienie procedur dochodzeniowych. ", " ", 2));
        mPytania.add(new pytanie(106, "Spośród wymienionych czynności, do zakresu pierwszej pomocy medycznej, udzielanej przez ratowników KSRG, nie należy: ", "A. wykonywanie pośredniego masażu serca. ", " ", "B. transport poszkodowanych do ośrodków leczniczych. ", " ", "C. tamowanie krwotoków. ", " ", "D. udrożnienie dróg oddechowych z użyciem rurki ustno-gardłowej. ", " ", "E. zapewnienie komfortu cieplnego.", " ", 2));
        mPytania.add(new pytanie(107, "Zadania z zakresu kwalifikowanej pierwszej pomocy realizowane przez ratowników KSRG: ", "A. zawsze w całości zastępują działania pogotowia ratunkowego. ", " ", "B. są uzupełnieniem działań fachowych jednostek ochrony zdrowia. ", " ", "C. są realizowane tylko i wyłącznie w przypadku technicznych trudności,", "uniemożliwiających dotarcie do poszkodowanego przez personel służby zdrowia.  ", "D. mogą być wykonywane tylko przy udziale koordynatora medycznych czynności ratowniczych. ", " ", "E. odbywają się zawsze pod nadzorem lekarza. ", " ", 2));
        mPytania.add(new pytanie(108, "Do zadań koordynatora medycznych działań ratowniczych KSRG należy:\n1) nadzorowanie działań ratowniczych w zakresie kwalifikowanej pierwszej pomocy prowadzonych przez podmioty i ratowników systemu na miejscu zdarzenia ;\n2) udzielanie pierwszej pomocy kwalifikowanej poszkodowanym;\n3) prowadzenie i ewentualne nadzorowanie segregacji, jako procesu określania priorytetów terapeutyczno-transportowych;\n4) przedstawianie kierującemu działaniem ratowniczym opinii dotyczących zabezpieczenia uczestników działań ratowniczych pod względem medycznym;\n5) transport poszkodowanych z kodem czerwonym do specjalistycznej placówki służby zdrowia. \nPrawidłowa odpowiedź to:", "A. 1,3,4. ", " ", "B. 1,2,3. ", " ", "C. 2,3,4. ", " ", "D. 3,4,5. ", " ", "E. 2,4,5.", " ", 1));
        mPytania.add(new pytanie(109, "Organizacja ratownictwa medycznego realizowanego przez podmioty KSRG nie obejmuje: ", "A. ujednolicenia zasad powiadamiania i dysponowania podmiotów systemu oraz podmiotów współdziałających z systemem.", " ", "B. ujednolicania zasad postępowania w zdarzeniach z dużą liczbą poszkodowanych. ", " ", "C. transportu lżej poszkodowanych do miejsca zamieszkania. ", " ", "D. ujednolicania metodyki planowania transportu poszkodowanej lub zagrożonej ludności do izb przyjęć lub szpit. oddziałów", " wyspec. w zakresie medycyny ratunkowej lub innych podmiotów prowadzących rat. med. w war. szpitalnych.", "E. wsparcia psychologicznego osób uczestniczących w działaniach ratowniczych. ", " ", 3));
        mPytania.add(new pytanie(110, "Kwalifikowana pierwsza pomoc to określenie czynności podejmowanych wobec osoby w stanie nagłego zagrożenia zdrowotnego przez ratownika. W rozumieniu Ustawy o Państwowym Ratownictwie Medycznym ratownikiem może być osoba:\n1) posiadająca wyłącznie dyplom szkoły lub uczelni medycznej;\n2) zatrudniona lub pełniąca służbę w jednostkach współpracujących z systemem ratownictwa medycznego lub będąca członkiem tych jednostek;\n3) posiadająca ważne zaświadczenie o ukończeniu kursu w zakresie kwalifikowanej pierwszej pomocy i uzyskaniu tytułu ratownika;\n4) której stan zdrowia pozwala na udzielanie kwalifikowanej pierwszej pomocy;\n5) posiadająca dyplom ukończenia szkoły medycznej oraz pełną zdolność do czynności prawnych.\nPrawidłowa odpowiedź to:", "A. 1,3,4. ", " ", "B. 1,2,3. ", " ", "C. 2,3,4. ", " ", "D. 3,4,5. ", " ", "E. 2,4,5.", " ", 3));
        mPytania.add(new pytanie(111, "Osoba udzielająca pierwszej pomocy, kwalifikowanej pierwszej pomocy oraz podejmująca medyczne czynności ratunkowe korzysta z ochrony przewidzianej prawem. ", "A. zawarte jest to w prawie zwyczajowym. ", " ", "B. wynika to z Rozporządzenie MSWiA w sprawie szczegółowych zasad organizacji", "krajowego systemu ratowniczo-gaśniczego z 1999 roku.  ", "C. wynika to z Kodeksu Karnego. ", " ", "D. wynika to z Ustawy o Państwowym Ratownictwie Medycznym z 2006 roku. ", " ", "E. nie podlega żadnej ochronie prawnej. ", " ", 4));
        mPytania.add(new pytanie(112, "Pozycja boczna ustalona ma na celu zapewnienie drożnych dróg oddechowych oraz umożliwienie swobodnego wypływu śliny i treści z jamy ustnej. Aby spełniała swoje zadanie muszą być spełnione warunki, z wyjątkiem: ", "A. pozycja musi być stabilna. ", " ", "B. musi być możliwe łatwe ocenianie czynności życiowych.", " ", "C. nie zmienia się raz przyjętego ułożenia poszkodowanego. ", " ", "D. musi być zapewniony swobodny wypływ śliny i innych wydzielin. ", " ", "E. należy unikać ucisku na klatkę piersiową. ", " ", 3));
        mPytania.add(new pytanie(113, "Jesteś świadkiem, jak osoba, która się krztusi traci przytomność i pada na podłoże . Powiadamiasz pogotowie ratunkowe, a następnie przystępujesz do działania w następującej kolejności: ", "A. obracasz poszkodowanego plecami ku górze i wykonujesz 5 uderzeń pomiędzy łopatkami. ", " ", "B. wykonujesz parę szybkich i bardzo silnych oddechów usta – usta. ", " ", "C. czekasz aż pacjent odzyska przytomność, podnosisz go,", "następnie stajesz za jego plecami i wykonujesz rękoczyn Heimlicha.  ", "D. udrażniasz drogi oddechowe i po stwierdzeniu braku ciała obcego w jamie ustnej", "oraz braku oddechu zaczynasz sztuczną wentylację.  ", "E. siadasz okrakiem na udach poszkodowanego i wykonujesz 5 uciśnięć nadbrzusza.", " ", 4));
        mPytania.add(new pytanie(114, "Kobieta 24-letnia połknęła garść pigułek nasennych około 30-45 minut temu. Znajdujesz ją nieprzytomną, wykonującą sporadyczne, łapiące powietrze, oddechy. Jej tętno jest szybkie i dobrze wypełnione. Co powinieneś zrobić? ", "A. zadzwonić po pogotowie ratunkowe i rozpocząć sztuczne oddychanie z częstością 10-12 oddechów na minutę. ", " ", "B. rozpocząć masaż serca, a po minucie zadzwonić po pogotowie ratunkowe. ", " ", "C. wykonać 2 uciśnięcia nadbrzusza, a następnie położyć pacjenta w pozycji bocznej ustalonej. ", " ", "D. podjąć reanimację przez 1 minutę, a następnie zadzwonić po pogotowie ratunkowe. ", " ", "E. wykonać 5 uciśnięć nadbrzusza, a następnie 2 wdechy. ", " ", 1));
        mPytania.add(new pytanie(115, "Udzielasz pomocy 6-letniej ofierze wypadku samochodowego, u której podejrzewasz uraz kręgosłupa szyjnego. Dziecko jest nieprzytomne, oddycha płytko, z wysiłkiem, słychać charczenie. Częstość oddechów 30 na minutę. Aby udrożnić drogi oddechowe powinieneś wykonać następujący manewr: ", "A. maksymalnie odgiąć głowę do tyłu. ", " ", "B. wysunąć żuchwę na szczękę. ", " ", "C. wyciągnąć język przed żuchwę. ", " ", "D. usiąść okrakiem na udach ratowanego dziecka i wykonywać uciśnięcia nadbrzusza. ", " ", "E. wykluczyć obecność ciała obcego i wysunąć żuchwę na szczękę jednocześnie stabilizując kręgosłup szyjny.", "Ewentualnie założyć rurkę ustno-gardłową.  ", 5));
        mPytania.add(new pytanie(116, "Zbliżasz się do osoby dorosłej, która leży na podłodze. Nie ma nikogo innego w pobliżu. Jak powinieneś postąpić? ", "A. ocenić przytomność leżącego, potem wezwać pogotowie ratunkowe. ", " ", "B. zadzwonić po pogotowie i czekać na karetkę przed domem. ", " ", "C. usunąć ewentualne ciała obce z dróg oddechowych palcem. ", " ", "D. prowadzić RKO przez 5 minut, a potem zadzwonić po pogotowie. ", " ", "E. zawołać o pomoc i zacząć oddechy zastępcze.", " ", 1));
        mPytania.add(new pytanie(117, "Wskaż odpowiedź fałszywą: ", "A. u tonących w pierwszej chwili po wydobyciu staramy się wylać całą wodę zgromadzoną w układzie oddechowym. ", " ", "B. jeżeli nie ma pewności co do mechanizmu tonięcia, zawsze zakładamy kołnierz ortopedyczny. ", " ", "C. również w porze letniej należy poszkodowanemu, wydobytemu z wody, zapewnić komfort cieplny. ", " ", "D. resuscytację u podtopionych zaczynamy od 5 oddechów zastępczych. ", " ", "E. po sprawdzeniu przytomności woła się o pomoc. ", " ", 1));
        mPytania.add(new pytanie(118, "U nieprzytomnego poszkodowanego, po urazie, bez żadnych krwotoków, z widocznym powierzchniowym otarciem skóry głowy, z zachowanym własnym wydolnym oddechem i tętnem - do działań pilnych należy:\n1) tlenoterapia;\n2) tylko dezynfekcja miejsca urazu i opatrunki;\n3) stabilizacja kręgosłupa szyjnego za pomocą kołnierza ortopedycznego;\n4) udrożnienie dróg oddechowych z założeniem rurki ustno-gardłowej\n5) prowadzenie oddechu zastępczego 12 na minutę.\nPrawidłowa odpowiedź to:", "A. 2,4,5. ", " ", "B. 1,3,4. ", " ", "C. 1,2,5. ", " ", "D. 3,4,5. ", " ", "E. 2,3,4. ", " ", 2));
        mPytania.add(new pytanie(119, "U niektórych poszkodowanych nieprzytomnych, ale oddychających stosuje się pozycję bezpieczną. Wybierz twierdzenie fałszywe: ", "A. pozycja ta może zapobiec dostaniu się treści pokarmowej do dróg oddechowych. ", " ", "B. pozycję taką stosuje się wyłącznie ze względu na łatwą kontrolę tętna u poszkodowanego. ", " ", "C. ułożenie poszkodowanego w tej pozycji nie gwarantuje przeżycia. ", " ", "D. możliwość ułożenia poszkodowanego w tej pozycji pozwala na udzielenie pomocy większej liczbie poszkodowanych. ", " ", "E. pozycja ta ułatwia samodzielne oddychanie u osoby nieprzytomnej.", " ", 2));
        mPytania.add(new pytanie(120, "Wskaż czynność, która nie jest elementem procedury udrażniania dróg oddechowych: ", "A. odessanie płynnej treści z jamy ustnej. ", " ", "B. rękoczyn Heimlicha. ", " ", "C. tlenoterapia bierna (maseczka tlenowa). ", " ", "D. założenie poszkodowanemu rurki ustno – gardłowej. ", " ", "E. odgięcie głowy poszkodowanego do tyłu. ", " ", 3));
        mPytania.add(new pytanie(121, "Jakie będzie twoje postępowanie u poszkodowanego, który w wyniku wypadku samochodowego doznał urazu z raną tłuczoną głowy, z przejściową utratą świadomości i przebywa nadal w samochodzie? ", "A. zadzwonię po pogotowie ratunkowe i będę czekać przy samochodzie obserwując poszkodowanego. ", " ", "B. po sprawdzeniu jego podstawowych funkcji życiowych (stan świadomości, oddech, krążenie) zawołam o pomoc,", "a następnie zależnie od sytuacji podejmę decyzję o ewakuacji lub udzieleniu pomocy w samochodzie.  ", "C. wyciągnę poszkodowanego z samochodu i zacznę oddechy zastępcze. ", " ", "D. podam tlen i będę czekać na pomoc. ", " ", "E. zacznę resuscytację w samochodzie. ", " ", 2));
        mPytania.add(new pytanie(122, "U nieprzytomnego poszkodowanego, po urazie, bez widocznych obrażeń, z zachowanym własnym charczącym oddechem i oznakami krążenia, po wezwaniu pomocy wykonasz następujące czynności:\n1) okrycie kocem termoizolacyjnym;\n2) tlenoterapia bierna;\n3) założenie rurki ustno-gardłowej;\n4) ręczna stabilizacja kręgosłupa szyjnego;\n5) udrożnienie dróg oddechowych poprzez wysunięcie żuchwy.\nPrawidłowa odpowiedź to:", "A. 2,5,3,1,4. ", " ", "B. 1,3,5,4,2. ", " ", "C. 3,2,4,1,5. ", " ", "D. 5,4,3,2,1. ", " ", "E. 4,2,1,3,5.", " ", 4));
        mPytania.add(new pytanie(123, "Postępowanie z poszkodowanym, który w czasie pożaru wyskoczył z III piętra i doznał urazu kręgosłupa w odcinku piersiowo-lędźwiowym oraz jest nieprzytomny, z zachowanym krążeniem i oddechem, w pierwszej kolejności obejmuje: ", "A. masaż pośredni serca, bo podtrzymanie krążenia jest priorytetem. ", " ", "B. oddech zastępczy z tlenoterapią o przepływie 10 l na minutę. ", " ", "C. stabilizację kręgosłupa oraz tlenoterapię bierną. ", " ", "D. opatrzenie ran i złamań oraz oparzeń. ", " ", "E. szybki transport do szpitala. ", " ", 3));
        mPytania.add(new pytanie(124, "Spośród niżej wymienionych czynności ratowniczych, u osoby nieprzytomnej wskaż priorytety: ", "A. opatrzenie rany oparzeniowej. ", " ", "B. udrożnienie dróg oddechowych. ", " ", "C. unieruchomienie złamanej kończyny. ", " ", "D. zaopatrzenie złamania otwartego. ", " ", "E. okrycie kocem termoizolacyjnym. ", " ", 2));
        mPytania.add(new pytanie(125, "U poszkodowanego, który nie reaguje na głos ani bodźce bólowe, ustalono szereg obrażeń i objawów. Zaznacz, który objaw (lub grupa objawów), albo obrażenie Twoim zdaniem jest najbardziej niepokojący i może wskazywać na potencjalne zagrożenie jego życia: ", "A. złamanie kończyny górnej. ", " ", "B. oparzenie okolicy goleni. ", " ", "C. rana dłoni z niewielkim, powolnym wyciekiem krwi. ", " ", "D. blada, chłodna i spocona skóra. ", " ", "E. zwichnięcie barku z jego stłuczeniem. ", " ", 4));
        mPytania.add(new pytanie(126, "Rurkę ustno-gardłową zastosujesz: ", "A. u poszkodowanych przytomnych, ale po urazach klatki piersiowej. ", " ", "B. u nieprzytomnych z obecną treścią pokarmową w jamie ustnej (dla udrożnienia). ", " ", "C. zawsze u poszkodowanych przytomnych z urazem twarzoczaszki. ", " ", "D. tylko przy bezdechu u dorosłych, zamiast tlenoterapii. ", " ", "E. u nieprzytomnych, aby zmniejszyć zagrożenie niedrożnością dróg oddechowych.", " ", 5));
        mPytania.add(new pytanie(127, "W postępowaniu z nieprzytomnym dorosłym nieurazowym, wezwanie pomocy powinno nastąpić: ", "A. po stwierdzeniu, że poszkodowany jest nieprzytomny. ", " ", "B. jeżeli poszkodowany ma poważne obrażenia. ", " ", "C. nie należy wzywać pomocy, jeżeli strażak-ratownik wie, co należy zrobić z osobą poszkodowaną. ", " ", "D. po pierwszej minucie resuscytacji. ", " ", "E. po udzieleniu pierwszej pomocy i stwierdzeniu, że stan poszkodowanego nie ulega zmianie. ", " ", 1));
        mPytania.add(new pytanie(128, "Brak świadomości to stan, w którym poszkodowany nie reaguje na bodźce zewnętrzne. Dla oceny głębokości tego stanu opracowano skalę Glasgow (GCS). Jakie parametry w niej oceniamy? ", "A. otwieranie oczu i reakcję na ból. ", " ", "B. otwieranie oczu, reakcję słowną, odpowiedź ruchową. ", " ", "C. odpowiedź ruchową i reakcję na głos. ", " ", "D. reakcję słowną, orientację w czasie i przestrzeni oraz reakcję na ból. ", " ", "E. reakcję słowną i otwieranie oczu. ", " ", 2));
        mPytania.add(new pytanie(129, "Właściwy rozmiar rurki ustno-gardłowej dla poszkodowanego nieprzytomnego wyznacza odległość: ", "A. od brody do obojczyka. ", " ", "B. od wcięcia przy płatku jego ucha do kącika ust. ", " ", "C. od czubka jego nosa do brody. ", " ", "D. rurkę dobiera się tylko u osoby przytomnej. ", " ", "E. rurki nie stosuje się u osób nieprzytomnych. ", " ", 2));
        mPytania.add(new pytanie(130, "Jeśli wystąpią trudności w prowadzeniu skutecznej wentylacji za pomocą maski i worka samorozprężalnego, należy:\n1) poprawić ułożenie maski na twarzy poszkodowanego;\n2) ponownie spróbować udrożnić drogi oddechowe za pomocą odpowiednich rękoczynów;\n3) poczekać z decyzją co, do dalszego postępowania na przybycie lekarza, gdyż możemy zaszkodzić poszkodowanemu;\n4) sprawdzić szczelność układu do wentylacji;\n5) pomimo trudności kontynuować wentylację, bo każdy manewr sprawdzający to strata czasu.\nPrawidłowa odpowiedź to:", "A. 2,4,5. ", " ", "B. 1,3,4. ", " ", "C. 2,3,4. ", " ", "D. 3,4,5. ", " ", "E. 1,2,4.", " ", 5));
        mPytania.add(new pytanie(131, "W czasie prowadzenia u poszkodowanego oddechu zastępczego przy użyciu maski twarzowej i worka samorozprężalnego, należy pamiętać o dołączeniu do zestawu rezerwuaru tlenowego. Ma to na celu: ", "A. zwiększenie stężenia tlenu w mieszaninie oddechowej z 21% do około 40-50 %. ", " ", "B. utrzymanie, przy właściwym dopływie tlenu, wysokiego stężenia tlenu w mieszaninie oddechowej,", "nawet na poziomie bliskim 100%.", "C. ułatwienie prowadzenia oddechu zastępczego (w takim przypadku nie trzeba dbać ", "o szczelne przyłożenie maski oddechowej do twarzy poszkodowanego).", "D. stworzenie wystarczającej rezerwy tlenowej na czas, nawet do 4 minut po odłączeniu przewodu,", "łączącego układ z butlą tlenową (co jest niezwykle przydatne, np. przy ewakuacji poszkodowanego).", "E. podanie prawidłowej ilości tlenu, przy jednocześnie występujących trudnościach ", "z utrzymaniem drożności dróg oddechowych poszkodowanego.", 2));
        mPytania.add(new pytanie(132, "Twoje biuro sponsoruje kurs pierwszej pomocy, podczas którego pracownicy uczą się resuscytacji (CPR) i Automatycznej Defibrylacji Zewnętrznej (AED). Ty uważasz, że: ", "A. pacjent z ostrym zawałem serca potrzebuje CPR, a nie AED. ", " ", "B. natychmiastowa CPR i wczesna defibrylacja (w ciągu 4-5 minut) ", "może znacznie zwiększyć liczbę uratowanych pacjentów z zatrzymaniem krążenia.  ", "C. jeśli defibrylacja była wykonana w ciągu 10 minut, CPR nie ma sensu. ", " ", "D. jeśli CPR było podjęte natychmiast, defibrylacja nie jest potrzebna. ", " ", "E. CPR należy wdrożyć dopiero po wykonaniu AED i to po analizie zrobionej przez defibrylator. ", " ", 2));
        mPytania.add(new pytanie(133, "Z kolegą zaczynasz resuscytację. Kolega pyta, czy na pewno należy wdrożyć CPR? Co musisz sprawdzić, by stwierdzić, że decyzja jest słuszna? ", "A. sprawdzić szerokość źrenic u poszkodowanego. ", " ", "B. sprawdzić obecność tętna na obwodzie. ", " ", "C. sprawdzić czy poszkodowany jest nieprzytomny i czy nie oddycha prawidłowo. ", " ", "D. sprawdzić czy skóra poszkodowanego jest nadal sina, zimna i spocona. ", " ", "E. skontrolować oznaki prawidłowego krążenia.", " ", 3));
        mPytania.add(new pytanie(134, "U dorosłych pacjentów prowadzenie resuscytacji krążeniowo – oddechowej w obserwowanym zatrzymaniu krążenia rozpoczynamy od: ", "A. 30 uciśnięć klatki piersiowej. ", " ", "B. 2 oddechów ratowniczych. ", " ", "C. 15 uciśnięć klatki piersiowej. ", " ", "D. 30 uciśnięć klatki piersiowej (1 ratownik) lub 15 uciśnięć klatki piersiowej (2 ratowników). ", " ", "E. 5 oddechów zastępczych. ", " ", 1));
        mPytania.add(new pytanie(135, "W celu uciskania klatki piersiowej podczas pośredniego masażu serca, ręce układamy: ", "A. na środku mostka poszkodowanego. ", " ", "B. na dolnym 1/3 końcu mostka poszkodowanego. ", " ", "C. na lewo od środka mostka nad sercem ratowanego. ", " ", "D. 1 cm powyżej łuku międzyżebrowego ratowanego. ", " ", "E. w górnej 1/3 części mostka poszkodowanego. ", " ", 1));
        mPytania.add(new pytanie(136, "Stosunek uciśnięć do oddechów ratowniczych u osoby dorosłej powinien wynosić: ", "A. 50:2. ", " ", "B. 30:2. ", " ", "C. 15:2. ", " ", "D. 10:1. ", " ", "E. 15:1. ", " ", 2));
        mPytania.add(new pytanie(137, "Podczas prowadzenia podstawowych zabiegów resuscytacyjnych, ocenę stanu poszkodowanego wykonuje się: ", "A. co jedną minutę. ", " ", "B. co 5 cykli (30 uciśnięć klatki piersiowej i 2 oddechy zastępcze). ", " ", "C. jeżeli poszkodowany zacznie prawidłowo oddychać. ", " ", "D. przed naklejeniem elektrod AED. ", " ", "E. po około 15 minutach resuscytacji.", " ", 3));
        mPytania.add(new pytanie(138, "Jeżeli pierwszy oddech ratowniczy nie powoduje uniesienia się klatki piersiowej, należy wykonać następujące czynności, z wyjątkiem: ", "A. sprawdzenia czy odgięcie głowy i uniesienie żuchwy są poprawnie wykonane. ", " ", "B. wygarnięcia „na ślepo” ewentualnych ciał obcych, będących przyczyną niedrożności dróg oddechowych. ", " ", "C. usunięcia tylko widocznych ciał obcych. ", " ", "D. wykonania nie więcej niż 5 prób wentylacji, jeśli są to oddechy wstępne u poszkodowanego urazowego. ", " ", "E. sprawdzenia czy nie występują uszkodzenia (bądź choroba) klatki piersiowej, uniemożliwiające jej prawidłowe unoszenie. ", " ", 2));
        mPytania.add(new pytanie(139, "W przypadku stwierdzenia u pacjenta objawów niedrożności dróg oddechowych spowodowanej prawdopodobnie obecnością ciała obcego w drogach oddechowych , działania ratownika polegają na: ", "A. zachęcaniu poszkodowanego do kaszlu. ", " ", "B. wykonaniu 5 uderzeń w okolicę międzyłopatkową. ", " ", "C. wykonaniu 5 uciśnięć nadbrzusza. ", " ", "D. wykonaniu 5 uderzeń w okolicę międzyłopatkową, a następnie 5 uciśnięć nadbrzusza. ", " ", "E. rytmicznym uderzaniu poszkodowanego w okolicę międzyłopatkową, na przemian z uciskaniem nadbrzusza. ", " ", 4));
        mPytania.add(new pytanie(140, "Worek samorozprężalny posiada następujące zalety, z wyjątkiem: ", "A. może być podłączony do maski twarzowej, rurki dotchawiczej", "lub alternatywnych przyrządów do udrażniania dróg oddechowych.  ", "B. pozwala osiągnąć wdechowe stężenie tlenu na poziomie bliskim 100%. ", " ", "C. pozwala osiągnąć wdechowe stężenie tlenu nie wyższe niż 75%. ", " ", "D. zabezpiecza przed zakażeniem ratownika prowadzącego wentylację. ", " ", "E. w razie uszkodzenia mechanicznego worka, można go zastosować tylko do tlenoterapii biernej.", " ", 3));
        mPytania.add(new pytanie(141, "Wykonujesz resuscytację z udziałem kolegi. Jak często powinieneś uciskać klatkę piersiową? ", "A. 50-60 razy na minutę. ", " ", "B. z częstością zależną od wielkości klatki piersiowej. ", " ", "C. około 100 razy na minutę. ", " ", "D. wystarczająco szybko by utrzymać różowy kolor skóry twarzy. ", " ", "E. 100-120 razy na minutę. ", " ", 3));
        mPytania.add(new pytanie(142, "Wykonujesz resuscytację u 5-letniego dziecka z zatrzymaniem czynności serca. Którą z technik masażu pośredniego serca powinieneś zastosować? ", "A. uciskanie jedną ręką z częstością 100 razy na minutę. ", " ", "B. uciskanie dwoma dłońmi tak, by odchylenie mostka nie przekraczało około 1,5 cm. ", " ", "C. uciskanie dolnego odcinka mostka tylko dwoma palcami. ", " ", "D. wykonanie 15 uciśnięć, a następnie 3 wolnych wdechów. ", " ", "E. wykonywanie uciśnięć jedną lub dwoma dłońmi (zależnie od uznania), z częstością około 100 razy na minutę. ", " ", 5));
        mPytania.add(new pytanie(143, "Bawisz się z dziećmi w pokoju, w którym jest dużo zabawek składających się z drobnych części. Nagle, 8-miesięczne niemowlę zaczyna się dusić. Słyszysz świst towarzyszący każdej próbie oddechu. Jego kaszel jest cichy i nieefektywny, zaczyna sinieć. Twoim zdaniem u niemowlęcia doszło do: ", "A. ostrego ataku astmy oskrzelowej. ", " ", "B. napadu padaczki. ", " ", "C. obrzęku strun głosowych w przebiegu infekcji. ", " ", "D. całkowitej niedrożności dróg oddechowych spowodowanej ciałem obcym. ", " ", "E. częściowej niedrożności dróg oddechowych.", " ", 5));
        mPytania.add(new pytanie(144, "Widziałeś jak koleżanka upadła na korytarzu w pracy. Stwierdziłeś, że jest nieprzytomna. Poprosiłeś drugą osobę, by wezwała pogotowie ratunkowe. Udrożniłeś drogi oddechowe. Aby ocenić czy nieprzytomna oddycha należy: ", "A. przyłożyć lusterko do ust nieprzytomnej. ", " ", "B. zbliżyć do ust i nosa poszkodowanej kartkę papieru lub piórko. ", " ", "C. ocenić ruchy tchawicy (jabłko Adama). ", " ", "D. patrzeć na ruchy klatki piersiowej , słuchać i wyczuwać czy jest obecny przepływ powietrza z nosa i ust. ", " ", "E. obserwować przez 5 sekund, czy unosi się klatka piersiowa. ", " ", 4));
